<script>
    export let github;
    export let npm;
    export let nuget;
    export let project;
    export let title;

    $: url = (label, color, logo) => {
        return `https://img.shields.io/static/v1?label=${label}&message=${!!project ? project : title}&color=${color}&logo=${logo}`;
    };
</script>

{#if (!!project || !!title) && (!!github || !!npm || !!nuget)}
    <ul>
        {#if (!!github)}
            <li>
                <a href={github} rel='noopener' target='_blank'>
                    <img src={url('GitHub', '181717', 'github')} alt='GitHub'/>
                </a>
            </li>
        {/if}
        {#if (!!npm)}
            <li>
                <a href={npm} rel='noopener' target='_blank'>
                    <img src={url('NPM', 'CB3837', 'npm')} alt='NPM'/>
                </a>
            </li>
        {/if}
        {#if (!!nuget)}
            <li>
                <a href={nuget} rel='noopener' target='_blank'>
                    <img src={url('NuGet', '004880', 'nuget')} alt='NuGet'/>
                </a>
            </li>
        {/if}
    </ul>
{/if}

<style>
    ul {
        list-style: none;
        margin: 0 0 1.625rem 0;
        min-height: 1.625rem;
        padding: 0;
    }

    ul li {
        display: inline-block;
        margin-right: 0.25rem;
    }
</style>