<script>
	import { stores } from '@sapper/app';
	import { beforeUpdate } from 'svelte';
	import { _, locale } from 'svelte-i18n';

    const { page } = stores();
    
	$: params = $page.path.split('/');

	let pathEN = path('en');
	let pathGA = path('ga');

	function path(targetLocale) {
		if (!$page.path || $page.path === '/')
			return `/${targetLocale}`;

		const params = $page.path.split('/');
		params[1] = targetLocale;
		return params.join('/');
	}

	beforeUpdate(() => {
		pathEN = path('en');
		pathGA = path('ga');
	});
</script>

<header>
    <ul class='meta-nav'>
		<li class='site-logo'>
			<a href={`/${$locale}`}>
				<img src='/gaois-white.svg' alt='Gaois'/>
				<span>{$_('global.documentation')}</span>
			</a>
		</li>
        <li class='gaois-item'>
            <a class='gaois-link' href={`https://www.gaois.ie/${$locale}`}>gaois.ie</a>
        </li>
		<li class='social-link twitter-link'>
			<a rel='noopener' target='_blank' href='https://twitter.com/gaois_ie'>
				<img width='29' height='25' src='/twitter.svg' alt='Twitter'/>
			</a>
		</li>
		<li class='social-link github-link'>
			<a rel='noopener' target='_blank' href='https://github.com/gaois'>
				<img width='30' height='30' src='/github.svg' alt='GitHub'/>
			</a>
		</li>
        <li class='language-switcher'>
            <ul>
                <li>
					<a class:current={$locale === 'ga'} href={pathGA}>
						<span class='language-name'>
							<span class='language-code'>Ga</span><span>eilge</span>
						</span>
					</a>
				</li>
                <li>
					<a class:current={$locale === 'en'} href={pathEN}>
						<span class='language-name'>
							<span class='language-code'>En</span><span>glish</span>
						</span>
					</a>
				</li>
            </ul>
        </li>
    </ul>
</header>

