	}

	.language-switcher ul li:first-of-type a::after {
		content: '|';
		position: absolute;
		right: -0.2rem;
	}

	.language-switcher .current {
		font-weight: 700;
	}

	.language-switcher ul li:first-of-type a.current::after {
