<script>
	import { stores } from '@sapper/app';
	import { menuOpen } from 'stores/menu';
    import { projectArticles, projectVersions } from 'stores/project';
    import { locale } from 'svelte-i18n';
	import { quintOut } from 'svelte/easing';
    import { slide } from 'svelte/transition';

    export let category;
    export let project;
    export let heading;
    export let latestVersion = '';
    export let index = '';
    export let singleton = false;

    const { page } = stores();
    const slideIn = { delay: 50, duration: 300, easing: quintOut };
    const slideOut = { delay: 100, duration: 300, easing: quintOut };

    $: currentProject = (!!$page.params && !!$page.params.project) ? $page.params.project : null;
    $: currentVersion = (!!$page.params && !!$page.params.version) ? $page.params.version : null;
    $: currentSlug = (!!$page.params && !!$page.params.slug) ? $page.params.slug : null;
</script>

{#if singleton}
    <a rel='prefetch' class:current={project === currentProject} on:click={() => $menuOpen = false}
        class='heading' href={`/${$locale}/${category}/${project}`}>
        {heading}
    </a>
{:else}
    {#if !!latestVersion}
        <a rel='prefetch'
            aria-haspopup='true' aria-expanded={project === currentProject}
            class:current={project === currentProject}
            class='heading'
            href={`/${$locale}/${category}/${project}/${latestVersion}/${index}`}>
            {heading}
        </a>
        {#if project === currentProject}
            <ol in:slide={slideIn} out:slide={slideOut}>
                {#each $projectVersions as version}
                    <li>
                        <a rel='prefetch'
                            aria-haspopup='true' aria-expanded={version === currentVersion}
                            class:current={version === currentVersion}
                            class='version'
                            href={`/${$locale}/${category}/${project}/${version}/${index}`}>{version}</a>
                        {#if version === currentVersion}
                            <ol>
                                {#each $projectArticles as article}
                                    <li>
                                        <a rel='prefetch'
                                            class:current={article.slug === currentSlug} on:click={() => $menuOpen = false}
                                            class='article'
                                            href={`/${$locale}/${category}/${project}/${version}/${article.slug}`}>
                                            {article.shortTitle ? article.shortTitle : article.title}
                                        </a>
                                    </li>
                                {/each}
                            </ol>
                        {/if}
                    </li>
                {/each}
            </ol>
        {/if}
    {:else}
        <a rel='prefetch'
            aria-haspopup='true' aria-expanded={project === currentProject}
            class:current={project === currentProject}
            class='heading'
            href={`/${$locale}/${category}/${project}/${index}`}>
            {heading}
        </a>
        {#if project === currentProject}
            <ol in:slide={slideIn} out:slide={slideOut}>
                {#each $projectArticles as article}
                    <li>
                        <a rel='prefetch'
                            class:current={article.slug === currentSlug} on:click={() => $menuOpen = false}
                            class='article' 
                            href={`/${$locale}/${category}/${project}/${article.slug}`}>
                            {article.shortTitle ? article.shortTitle : article.title}
                        </a>
                    </li>
                {/each}
            </ol>
        {/if}
    {/if}
{/if}

<style>
    a {
        display: block;
        padding: 0.5em;
        text-decoration: none;
    }

	ol {
        list-style: none;
		margin: 0 0 0 1rem;
		padding: 0;
    }

    ol ol {
		color: #555;
		text-transform: none;
    }
    
    .heading {
		margin-top: 0;
		padding: 0.5em;
    }

    .current {
        color: var(--secondary-color);
        font-weight: 700;
    }
</style>