<script>
    import { stores } from '@sapper/app';
    import { _ } from 'svelte-i18n';
    import { menuOpen } from 'stores/menu';
    import { tocExpanded } from 'stores/toc';
    
    export let toc;
    
    const { page } = stores();
    
    $: expanded = $tocExpanded;

    function toggle() {
        if ($menuOpen) $menuOpen = false;
        $tocExpanded = !$tocExpanded;
    }
</script>

{#if !!toc}
    <nav id='toc' class:expanded>
        <h2>{$_(`document.inThisArticle`)}</h2>
        <div>{@html toc}</div>
        <a class='top' href={`${$page.path}#`}>{$_(`document.backToTop`)}</a>
    </nav>
    <button id='toc-button' on:click={() => toggle()} class:expanded
        aria-controls='toc' aria-expanded={expanded} aria-haspopup='true'>
        {$_(`document.inThisArticle`)}
    </button>
{/if}

