import { get } from 'svelte/store';
import { locale } from 'services/i18n';

export const errorMessage = (statusCode, localeCode = null) => {
    const loc = !localeCode ? get(locale) : localeCode;

    switch (statusCode) {
        case 400:
            return (loc === 'ga') ? 'Iarratas neamhbhailí' : 'Invalid request';
        case 401:
            return (loc === 'ga') ? 'Neamhúdaraithe' : 'Unauthorized';
        case 404:
            return (loc === 'ga') ? 'Níor aimsíodh an leathanach' : 'The page was not found';
        default:
            return (loc === 'ga') ? 'Earráid inmheánach ar an bhfreastalaí' : 'Internal server error';
    }
};