export default function slugify(str) {
    str = str.replace(/^\s+|\s+$/g, '');
    str = str.toLowerCase();

    const ORIGIN = "ãàáäâẽèéëêìíïîõòóöôùúüûñç·/_,:;";
    const TARGET = "aaaaaeeeeeiiiiooooouuuunc------";

    for (let i = 0, l = ORIGIN.length; i < l; i++) {
        str = str.replace(new RegExp(ORIGIN.charAt(i), 'g'), TARGET.charAt(i));
    }

    str = str
        .replace(/[^a-z0-9 -]/g, '')
        .replace(/\s+/g, '-')
        .replace(/-+/g, '-');

    return str;
}